import { Component, OnInit } from '@angular/core';
import { Proyectos } from 'src/app/model/proyectos';
import { ProyectosService } from 'src/app/service/proyectos.service';
import { TokenService } from 'src/app/service/token.service';

@Component({
  selector: 'app-proyecto',
  templateUrl: './proyecto.component.html',
  styleUrls: ['./proyecto.component.css']
})
export class ProyectoComponent implements OnInit{
  proyectos: Proyectos[] = [];

  constructor(private sProyectos: ProyectosService, private tokenService: TokenService) { }
  isLogged = false;

  ngOnInit(): void {
    this.cargarProyectos();
    if(this.tokenService.getToken()){
      this.isLogged = true;
    } else {
      this.isLogged = false;
    }
  }

  cargarProyectos(): void{
    this.sProyectos.lista().subscribe(
      data =>{
        this.proyectos = data;
      }
    )
  }

  delete(id?: number){
    if( id != undefined){
      this.educacionS.delete(id).subscribe(
        data => {
          this.cargarEducacion();
        }, err => {
          alert("No se pudo eliminar");
        }
      )
    }
  }
}
<div class="row">
    <h2>Proyectos realizados</h2>
    <div class="contenedor">
        <h3>Sistema de gestión de club</h3>
        <div class="col-sm-12 col-md-4 col-lg-4 parte1">     
            <p>Proyecto realizado para gestionar el club de conquistadores y sus miembros. <br>Realizado utilizando HTML, CSS, Javascript, PHP, mySQL.</p>
        </div>
        <div class="col-sm-12 col-md-6 col-lg-6 offset-md-1 offset-lg-1 parte2">
            <a href="https://club-de-conquistadores.000webhostapp.com/index.php" target="_blank"><img src="../../../assets/gestion-de-club.PNG" class="img-fluid img-proyecto" alt="pagina principal de sistema de gestion de club"></a>            
        </div>
    </div>
    <div class="contenedor">
        <h3>Portfolio personal</h3>
        <div class="col-sm-12 col-md-4 col-lg-4 parte1">     
            <p>Proyecto final de Argentina Programa #YoProgramo</p>
        </div>
        <div class="col-sm-12 col-md-6 col-lg-6 offset-md-1 offset-lg-1 parte2">
            <a href="#inicio"><img src="../../../assets/proyectoAP.PNG" class="img-fluid img-proyecto" alt="pagina principal de sistema de gestion de club"></a>            
        </div>
    </div>
</div>
