import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';


@Component({
  selector: 'app-logo-arg-prog',
  templateUrl: './logos.component.html',
  styleUrls: ['./logos.component.css']
})
export class LogoArgProgComponent implements OnInit {
  isLogged = false;

  constructor(private router:Router) { }

  ngOnInit(): void {
    // if(this.tokenService.getToken()){
    //   this.isLogged = true;
    // }else{
    //   this.isLogged = false;
    // }
  }

  onLogOut():void{
    // this.tokenService.logOut();
    window.location.reload();
  }

  login(){
    this.router.navigate(['/login'])
  }

}
<div class="row">
    <div class="container">
        <div class="logo">
            <img src="../../../assets/APLogo.png" id= "logoAP" alt="Logo de Argentina Programa">
        </div>
        <div class="derecha">
            <div class="redes">
                <a href=""><img src="../../../assets/github.png" id="github" alt=""></a>
                <a href=""><img src="../../../assets/linkedin.png" id="linkedin" alt=""></a>
                <a href=""><img src="../../../assets/facebook.png" id="facebook" alt=""></a>
                <a href=""><img src="../../../assets/twitter.png" id="twitter" alt=""></a>
                <a href=""><img src="../../../assets/instagram.png" id="instagram" alt=""></a>
            </div>
            <div class="ingreso">
                <button type="button" class="btn btn-primary" (click)="login()">Iniciar Sesión</button>
                <!-- <button type="button" class="btn btn-primary" (click)="login()" *ngIf="!isLogged">Iniciar Sesión</button> -->
                <!-- <button type="button" class="btn btn-primary" (click)="onLogOut()" *ngIf="isLogged">Cerrar Sesión</button> -->
            </div>
        </div>

    </div>
</div>
